mapper.select:
select
  customer_id, wx_id, wx_name, customer_phone, real_num, real_name, state
from speedlogisticsdb.t_customer


        and customer_id = #{customerId}


        and wx_id = #{wxId}


        and wx_name = #{wxName}


        and customer_phone = #{customerPhone}


        and real_num = #{realNum}


        and real_name = #{realName}


        and state = #{state}

mapper.update:
update speedlogisticsdb.t_customer
set wx_name = #{wxName}
where customer_id = #{customerId}

mapper.select:
SELECT * FROM `t_customer`
WHERE `wx_name` LIKE CONCAT(#{condition},'%') OR `real_name` LIKE CONCAT(#{condition},'%')
LIMIT #{startRow}, #{pageSize}
