mapper.select:
select
  staff_id, wx_id, staff_name, staff_login_name, staff_password, staff_age, staff_sex, staff_phone, hiredate, dot_id, state
from speedlogisticsdb.t_staff


        and staff_id = #{staffId}


        and wx_id = #{wxId}


        and staff_name = #{staffName}


        and staff_login_name = #{staffLoginName}


        and staff_password = #{staffPassword}


        and staff_age = #{staffAge}


        and staff_sex = #{staffSex}


        and staff_phone = #{staffPhone}


        and hiredate = #{hiredate}


        and dot_id = #{dotId}


        and state = #{state}
